mapper.select:

SELECT * FROM (
    SELECT I.INQUIRY_TYPE, I.INQUIRY_TITLE, ROWNUM AS RNUM
    FROM (
        SELECT INQUIRY_TYPE, INQUIRY_TITLE
        FROM TBL_INQUIRY
        WHERE MEMBER_ID = #{memberId}
        ORDER BY ID DESC
    ) I
    WHERE ROWNUM <= #{pagination.endRow}
)
WHERE RNUM >= #{pagination.startRow}
