mapper.select:
SELECT ID, PLAN_GRADE, PLAN_PRICE, PLAN_BOX_SIZE, PLAN_MAX_MEMBERS, PLAN_STATUS, CREATED_DATE, UPDATED_DATE, PLAN_DELIVERY_FEE
FROM TBL_PLAN
WHERE ID = #{id}

SELECT ID, MEMBER_NAME, MEMBER_EMAIL, MEMBER_PHONE_NUMBER, MEMBER_STATUS, MEMBER_PASSWORD, CREATED_DATE, UPDATED_DATE, MEMBER_ADDRESS_CODE, MEMBER_ADDRESS_BASIC, MEMBER_ADDRESS_DETAIL, MEMBER_TYPE
FROM TBL_MEMBER
WHERE ID = #{id}
